import base64, zlib

def decode_layer(blob):
    return zlib.decompress(base64.b64decode(blob[::-1]))

blob = b""

for i in range(100):
    try:
        code = decode_layer(blob)
        print(f"\n[Layer {i+1}] =========================\n")
        print(code.decode(errors="ignore"))
        
        if b"exec((_)(b'" in code:
            start = code.find(b"exec((_)(b'") + len(b"exec((_)(b'")
            end = code.find(b"'))", start)
            blob = code[start:end]
        else:
            break
    except Exception as e:
        print("Stopped:", e)
        break

